import { useState } from 'react'

export default function Calculator() {

    const [display, setDisplay] = useState(0);

    let currentCalc = '';

    const handleClick = (event) => {
        event.preventDefault();
        setDisplay(event.id);
        currentCalc = currentCalc + event.target.id;
        console.log(currentCalc);
    }

    const handleSubmit = () => {
        setDisplay(eval(currentCalc));
    }

    const resetCalc = () =>{
        setDisplay(0);
        currentCalc="";
    }

    return (
        <div id="calcContainer">
            <section id="result"><h1>0</h1></section>
            <section id="buttons-container">
                <div className="calc-button tertiary-btn" onClick={resetCalc}>AC</div>
                <div className="calc-button tertiary-btn"><span className="material-symbols-rounded size-48">backspace</span></div>
                <div className="calc-button tertiary-btn" id="%" onClick={handleClick}>&percnt;</div>
                <div className="calc-button primary-btn" id="/" onClick={handleClick}>&div;</div>
                <div className="calc-button primary-container-btn" id="7" onClick={handleClick}>7</div>
                <div className="calc-button primary-container-btn" id="8" onClick={handleClick}>8</div>
                <div className="calc-button primary-container-btn" id="9" onClick={handleClick}>9</div>
                <div className="calc-button primary-btn" id="*" onClick={handleClick}>&times;</div>
                <div className="calc-button primary-container-btn" id="4" onClick={handleClick}>4</div>
                <div className="calc-button primary-container-btn" id="5" onClick={handleClick}>5</div>
                <div className="calc-button primary-container-btn" id="6" onClick={handleClick}>6</div>
                <div className="calc-button primary-btn" id="-" onClick={handleClick}>&minus;</div>
                <div className="calc-button primary-container-btn" id="1" onClick={handleClick}>1</div>
                <div className="calc-button primary-container-btn" id="2" onClick={handleClick}>2</div>
                <div className="calc-button primary-container-btn" id="3" onClick={handleClick}>3</div>
                <div className="calc-button primary-btn" id="+" onClick={handleClick}>&plus;</div>
            </section>
            <section id="bottom-btns">
                <div className="calc-button primary-container-btn" id="zero-btn" id="0" onClick={handleClick}>0</div>
                <div className="calc-button primary-container-btn" id="." onClick={handleClick}>&period;</div>
                <div className="calc-button primary-btn" id="=" onClick={handleSubmit}>&equals;</div>
            </section>
        </div>
    )
}

